import Head from 'next/head'
import styles from './layout.module.css'
import utilStyles from '../styles/utils.module.css'
import { getSortedSubpagesData } from '../lib/subpages'
import Link from 'next/link'
import NavItem from './navItem'

const name = 'James'
export const siteTitle = 'james168ma'

export default function Layout({ children, home, allSubpagesData }) {
  return (
    <div className={styles.container}>
      <Head>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <header className={styles.header}>
        {home ? (
          <>
            <img
              src=""
              className={`${styles.headerHomeImage} ${utilStyles.borderCircle}`}
              alt={name}
            />
            <h1 className={utilStyles.heading2Xl}>{name}</h1>
          </>
        ) : (
          <>
            <Link href="/">
              <a>
                <img
                  src="/images/profile.jpg"
                  className={`${styles.headerImage} ${utilStyles.borderCircle}`}
                  alt={name}
                />
              </a>
            </Link>
            <h2 className={utilStyles.headingLg}>
              <Link href="/">
                <a className={utilStyles.colorInherit}>{name}</a>
              </Link>
            </h2>
          </>
        )}
      </header>

      <nav className={styles.navbar}>
        <ul className={styles.navbarNav}>
          <NavItem link="/" text="Home" selected={true}/>  /*edit this later*/
          <NavItem link="/" text="Hackathons" />
          <NavItem link="/" text="UCLA" />
          <NavItem link="/" text="APCS" />
        </ul>
      </nav>

      <main>{children}</main>
      {!home && (
        <div className={styles.backToHome}>
          <Link href="/">
            <a>‚Üê Back to home</a>
          </Link>
        </div>
      )}
    </div>
  );
}

export async function getStaticProps() {
  const allSubpagesData = getSortedSubpagesData()
  return {
    props: {
      allSubpagesData
    }
  }
}
